import React from "react";
import './WeatherInfo.scss'
import {
  useNavigate,
  useLocation
} from "react-router-dom";
import { useRef, useState } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";

import "./styles.css";

// import required modules
import { Pagination } from "swiper";

function fg() {
  return (
    <>
      <Swiper
        slidesPerView={3}
        spaceBetween={30}
        pagination={{
          clickable: true,
        }}
        modules={[Pagination]}
        className="mySwiper"
      >
        <SwiperSlide>Slide 1</SwiperSlide>
        <SwiperSlide>Slide 2</SwiperSlide>
        <SwiperSlide>Slide 3</SwiperSlide>
        <SwiperSlide>Slide 4</SwiperSlide>
        <SwiperSlide>Slide 5</SwiperSlide>
        <SwiperSlide>Slide 6</SwiperSlide>
        <SwiperSlide>Slide 7</SwiperSlide>
        <SwiperSlide>Slide 8</SwiperSlide>
        <SwiperSlide>Slide 9</SwiperSlide>
      </Swiper>
    </>
  );
}


function g() {
    var now = new Date();
    var days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    var x = now.getDay();
    if(x+2 > 6) {
        x=x+2-7
    } else {
        x=x+2;
    }
    var day = days[x];
    return day;
}
function day(n) {
    const today = new Date()
    const tomorrow = new Date(today)
    tomorrow.setDate(tomorrow.getDate() + n)
    var days = ['Jan', 'Feb', 'Mar','Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', , 'Oct', 'Nov', 'Dec'];
    return days[tomorrow.getMonth()] + " " + tomorrow.getDate();
}
class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        };
    }

    componentDidMount() {
        fetch(
            "https://geocode.xyz/" + this.props.city.replace(/ /g, '_') + "?json=1")
            .then((res1) => res1.json())
            .then((json1) => {
                fetch(
                    "https://weather-react-api.vercel.app/forecast/coords/"+json1["latt"]+"," + json1["longt"])
                    .then((res) => res.json())
                    .then((json) => {
                        this.setState({
                            items: json,
                            DataisLoaded: true
                        });
                    })
            })
    }
    render() {
        const { DataisLoaded, items } = this.state;

        return (
           <fg/>

    );
}
}
function WeatherInfo() {
        var location = useLocation();

        return (
            <body>
            <App city={location.state.city}/>
            </body>
        );
}

export default WeatherInfo;
