import React from "react";
import './WeatherInfo.scss'
import {
  useNavigate,
  useLocation
} from "react-router-dom";
import { useEffect, useRef } from "react";
import {ReactScrollableList} from "react-scrollable-list";


function toCelsius(F) {
    return (F-32)/1.8000
}

function g() {
    var now = new Date();
    var days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    var x = now.getDay();
    if(x+2 > 6) {
        x=x+2-7
    } else {
        x=x+2;
    }
    var day = days[x];
    return day;
}
function day(n) {
    const today = new Date()
    const tomorrow = new Date(today)
    tomorrow.setDate(tomorrow.getDate() + n)
    var days = ['Jan', 'Feb', 'Mar','Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', , 'Oct', 'Nov', 'Dec'];
    return days[tomorrow.getMonth()] + " " + tomorrow.getDate();
}
class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        };
    }

    componentDidMount() {
        fetch(
            "http://api.openweathermap.org/geo/1.0/direct?q="+this.props.city.replace(/ /g, '_') +"&limit=5&appid=ebd7ba44716d363e4cf939b5c8a73e46")
            .then((res1) => res1.json())
            .then((json1) => {
                if(json1[0]["lat"] == undefined){
                    this.setState({
                        items: [],
                        DataisLoaded: true
                    })
                    return;
                }
                fetch(
                    "https://weather-react-api.vercel.app/forecast/coords/"+json1[0]["lat"]+"," + json1[0]["lon"])
                    .then((res) => res.json())
                    .then((json) => {
                        this.setState({
                            items: json,
                            DataisLoaded: true
                        });
                    })
            })
    }

    render() {
        const { DataisLoaded, items } = this.state;
        if (!DataisLoaded) return <div>
            <h1 className="loading"> Loading... </h1> </div> ;
        if(items == [])
            window.open("/", "_self");
        return (<div class="card">

        <h2>{this.props.city.charAt(0).toUpperCase() + this.props.city.slice(1)}</h2>
        <h3>{this.state.items["currently"]["summary"] + ", Feels like " + Math.round(toCelsius(this.state.items["currently"]["apparentTemperature"])) + "°"}<br/>
        {"Wind: " + Math.round(this.state.items["currently"]["windSpeed"]) + "kph"} <br/>{"UV Index: " + this.state.items["currently"]["uvIndex"]}<br/> {"Pressure: " + this.state.items["currently"]["pressure"] +  "hPa"}</h3>
        <br/><br/><br/><br/><br/>
        <h1>{Math.round(toCelsius(this.state.items["currently"]["temperature"])) + "°"}</h1>

        <table>
            <tr>
                <td>{day(1)}</td>
                <td>{day(2)}</td>
                <td>{day(3)}</td>
                <td>{day(4)}</td>
                <td>{day(5)}</td>
            </tr>
            {/* high */}
            <tr>
                <td>{Math.round(toCelsius(this.state.items["daily"]["data"][1]["temperatureHigh"])) + "°"}</td>
                <td>{Math.round(toCelsius(this.state.items["daily"]["data"][2]["temperatureHigh"])) + "°"}</td>
                <td>{Math.round(toCelsius(this.state.items["daily"]["data"][3]["temperatureHigh"])) + "°"}</td>
                <td>{Math.round(toCelsius(this.state.items["daily"]["data"][4]["temperatureHigh"])) + "°"}</td>
                <td>{Math.round(toCelsius(this.state.items["daily"]["data"][5]["temperatureHigh"])) + "°"}</td>
            </tr>
            {/* low */}
            <tr>
                <td>{Math.round(toCelsius(this.state.items["daily"]["data"][1]["temperatureLow"])) + "°"}</td>
                <td>{Math.round(toCelsius(this.state.items["daily"]["data"][2]["temperatureLow"])) + "°"}</td>
                <td>{Math.round(toCelsius(this.state.items["daily"]["data"][3]["temperatureLow"])) + "°"}</td>
                <td>{Math.round(toCelsius(this.state.items["daily"]["data"][4]["temperatureLow"])) + "°"}</td>
                <td>{Math.round(toCelsius(this.state.items["daily"]["data"][5]["temperatureLow"])) + "°"}</td>
            </tr>
        </table>
    </div>

    );
}
}
function WeatherInfo() {
        var location = useLocation();

        return (
            <body>
            <App city={location.state.city}/>
            </body>
        );
}

export default WeatherInfo;
