import React from "react";
import './WeatherInfo.scss'
import {
  useNavigate,
  useLocation
} from "react-router-dom";


function g() {
    var now = new Date();
    var days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    var x = now.getDay();
    if(x+2 > 6) {
        x=x+2-7
    } else {
        x=x+2;
    }
    var day = days[x];
    return day;
}
function day(n) {
    const today = new Date()
    const tomorrow = new Date(today)
    tomorrow.setDate(tomorrow.getDate() + n)
    var days = ['Jan', 'Feb', 'Mar','Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', , 'Oct', 'Nov', 'Dec'];
    return days[tomorrow.getMonth()] + " " + tomorrow.getDate();
}
class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        };
    }

    componentDidMount() {
        fetch(
            "https://geocode.xyz/" + this.props.city.replace(/ /g, '_') + "?json=1")
            .then((res1) => res1.json())
            .then((json1) => {
                fetch(
                    "https://weather-react-api.vercel.app/forecast/coords/"+json1["latt"]+"," + json1["longt"])
                    .then((res) => res.json())
                    .then((json) => {
                        this.setState({
                            items: json,
                            DataisLoaded: true
                        });
                    })
            })
    }
    render() {
        const { DataisLoaded, items } = this.state;
        if (!DataisLoaded) return <div>
            <h1 className="loading"> Loading... </h1> </div> ;
        return (
            <div className="ds">
                <head>

                  <link rel="stylesheet" href="/path/to/flickity.css" media="screen"/>
</head>
               <div class="gallery js-flickity"
  data-flickity-options='{ "wrapAround": true }'>
  <div class="gallery-cell"></div>
  <div class="gallery-cell"></div>
  <div class="gallery-cell"></div>
  <div class="gallery-cell"></div>
  <div class="gallery-cell"></div>
</div>
            </div>

    );
}
}
function WeatherInfo() {
        var location = useLocation();

        return (
            <body>
            <App city={location.state.city}/>
            </body>
        );
}

export default WeatherInfo;
