import React from "react";
import './WeatherInfo.scss'
import {
  useNavigate,
  useLocation
} from "react-router-dom";

function g() {
    var now = new Date();
    var days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    var x = now.getDay();
    if(x+2 > 6) {
        x=x+2-7
    } else {
        x=x+2;
    }
    var day = days[x];
    return day;
}
class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        };
    }

    componentDidMount() {
        fetch(
            "https://goweather.herokuapp.com/weather/" + this.props.city)
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    items: json,
                    DataisLoaded: true
                });
            })
    }
    render() {
        const { DataisLoaded, items } = this.state;
        if (!DataisLoaded) return <div>
            <h1> Loading </h1> </div> ;

        return (

            <div class="property-card-container">
                <article class="widget">
  <div class="weatherIcon"><h1>s</h1></div>
  <div class="weatherInfo">
    <div class="temperature"><span>{this.state.items["temperature"]}</span></div>
    <div class="description">    
      <div class="weatherCondition">{this.state.items["description"]}</div>    
      <div class="place">New York, New York</div>
    </div>
  </div>
  <div class="date">Today</div>
</article>




  <div class="property-card">

    <div class="property-details">
      <div class="property-title">
        <h4>Today</h4><br></br>
      </div>
      <div class="property-rentLevels">


        <div class="rent-level1">
          <p>Temperature:</p>
          <p><b>{this.state.items["temperature"]}</b></p>
        </div>

        <div class="rent-level2">
        <p>Desc:</p>
          <p><b>{this.state.items["description"]}</b></p>

        </div>

        <div class="rent-level3">
        <p>Wind:</p>
          <p><b>{this.state.items["wind"]}</b></p>
        </div>


      </div>
    </div>
    <br/> <br/>
    <div class="property-details">
      <div class="property-title">
        <h4>Tomorrow</h4><br></br>
      </div>
      <div class="property-rentLevels">


        <div class="rent-level1">
          <p>Temperature:</p>
          <p><b>{this.state.items["forecast"][0]["temperature"]}</b></p>
        </div>

        <div class="rent-level2">
        <p>Wind:</p>
          <p><b>{this.state.items["forecast"][0]["wind"]}</b></p>

        </div>

      </div>
    </div>
    <br/> <br/>
    <div class="property-details">
      <div class="property-title">
        <h4>{g()}</h4><br></br>
      </div>
      <div class="property-rentLevels">


        <div class="rent-level1">
          <p>Temperature:</p>
          <p><b>{this.state.items["forecast"][1]["temperature"]}</b></p>
        </div>

        <div class="rent-level2">
        <p>Wind:</p>
          <p><b>{this.state.items["forecast"][1]["wind"]}</b></p>

        </div>

      </div>
    </div>


  </div>
  </div>
    );
}
}
function WeatherInfo() {
        var location = useLocation();

        return (
            <body>
            <App city={location.state.city}/>
            </body>
        );
}

export default WeatherInfo;
