import React from "react";
import './WeatherInfo.scss'
import {
  useNavigate,
  useLocation
} from "react-router-dom";


function g() {
    var now = new Date();
    var days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    var x = now.getDay();
    if(x+2 > 6) {
        x=x+2-7
    } else {
        x=x+2;
    }
    var day = days[x];
    return day;
}
function day(n) {
    const today = new Date()
    const tomorrow = new Date(today)
    tomorrow.setDate(tomorrow.getDate() + n)
    var days = ['Jan', 'Feb', 'Mar','Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', , 'Oct', 'Nov', 'Dec'];
    return days[tomorrow.getMonth()] + " " + tomorrow.getDate();
}
class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        };
    }

    componentDidMount() {
        fetch(
            "https://geocode.xyz/" + this.props.city.replace(/ /g, '_') + "?json=1")
            .then((res1) => res1.json())
            .then((json1) => {
                fetch(
                    "https://weather-react-api.vercel.app/forecast/coords/"+json1["latt"]+"," + json1["longt"])
                    .then((res) => res.json())
                    .then((json) => {
                        this.setState({
                            items: json,
                            DataisLoaded: true
                        });
                    })
            })
    }
    render() {
        const { DataisLoaded, items } = this.state;
        if (!DataisLoaded) return <div>
            <h1 className="loading"> Loading... </h1> </div> ;
        return (
            <div className="ds">
                <fieldset class="slideshow">

                <fieldset class="slideshow">


<input type="radio" id="slideCheckbox1" name="slide" checked autofocus></input>
<div class="slide">
  <div class="slide__html">

  </div>
  <div class="slide__content">
    <h1>Slideshow Concept V2 (No JS)</h1>
    <p>A pure CSS and HTML slideshow concept.</p>
    <p>To add or remove slides:</p>
    <ul>
      <li>Add a new slide template and label in the HTML</li>
      <li>Update the <code>$slide-count</code> SCSS variable</li>
      <li>Tab colours: Update the <code>$c-slides</code> SCSS variable</li>
      <li>Slide popout images: Update the <code>$b-slides</code> SCSS variable</li>
    </ul>
    <p>Use the tabs below to change slide</p>
    <p><strong style="color: #0F0">NEW:</strong> Arrow keys work too after your first selection.</p>
    <p>Et voila.</p>
  </div>  
</div>


<input type="radio" id="slideCheckbox2" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>More</h1>
    <p>More here</p>
  </div> 
</div>


<input type="radio" id="slideCheckbox3" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>Yet More</h1>
    <p>Yet more here</p>
  </div>  
</div>


<input type="radio" id="slideCheckbox4" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>Zzz</h1>
    <p>Yada yada</p>
  </div>   
</div>

<input type="radio" id="slideCheckbox5" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>The end</h1>
    <p>It's over</p>
  </div>  
</div>

<nav>

  <label class="slide-button" for="slideCheckbox1">Intro</label>
  <label class="slide-button" for="slideCheckbox2">More</label>
  <label class="slide-button" for="slideCheckbox3">Yet More</label>
  <label class="slide-button" for="slideCheckbox4">Zzz</label>
  <label class="slide-button" for="slideCheckbox5">The End</label>
</nav>

</fieldset>



<label class="slide-button" for="slideCheckbox2">More</label>
<input type="radio" id="slideCheckbox2" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>More</h1>
    <p>More here</p>
  </div> 
</div>


<label class="slide-button" for="slideCheckbox3">Yet More</label>
<input type="radio" id="slideCheckbox3" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>Yet More</h1>
    <p>Yet more here</p>
  </div>  
</div>

<label class="slide-button" for="slideCheckbox4">Zzz</label>
<input type="radio" id="slideCheckbox4" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>Zzz</h1>
    <p>Yada yada</p>
  </div>   
</div>

<label class="slide-button" for="slideCheckbox5">The End</label>
<input type="radio" id="slideCheckbox5" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>The end</h1>
    <p>It's over</p>
  </div>  
</div>


</fieldset>


            </div>

    );
}
}
function WeatherInfo() {
        var location = useLocation();

        return (
            <body>
            <App city={location.state.city}/>
            </body>
        );
}

export default WeatherInfo;
