import React from "react";
import './WeatherInfo.scss'
import {
  useNavigate,
  useLocation
} from "react-router-dom";


function g() {
    var now = new Date();
    var days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    var x = now.getDay();
    if(x+2 > 6) {
        x=x+2-7
    } else {
        x=x+2;
    }
    var day = days[x];
    return day;
}
function day(n) {
    const today = new Date()
    const tomorrow = new Date(today)
    tomorrow.setDate(tomorrow.getDate() + n)
    var days = ['Jan', 'Feb', 'Mar','Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', , 'Oct', 'Nov', 'Dec'];
    return days[tomorrow.getMonth()] + " " + tomorrow.getDate();
}
class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        };
    }

    componentDidMount() {
        fetch(
            "https://geocode.xyz/" + this.props.city.replace(/ /g, '_') + "?json=1")
            .then((res1) => res1.json())
            .then((json1) => {
                fetch(
                    "https://weather-react-api.vercel.app/forecast/coords/"+json1["latt"]+"," + json1["longt"])
                    .then((res) => res.json())
                    .then((json) => {
                        this.setState({
                            items: json,
                            DataisLoaded: true
                        });
                    })
            })
    }
    render() {
        const { DataisLoaded, items } = this.state;
        if (!DataisLoaded) return <div>
            <h1 className="loading"> Loading... </h1> </div> ;
        return (
            <div className="ds">
                <fieldset class="slideshow">


<label class="slide-button" for="slideCheckbox1">Intro</label>
<input type="radio" id="slideCheckbox1" name="slide" checked></input>
<div class="slide">
  <div class="slide__content">
    <span class="psst">
      <strong>Psst!</strong> Updated version of this pen available
      <a href="https://codepen.io/pbutcher/pen/yLLKbNo" target="_blank">here</a>!
    </span>
    <h1>Slideshow Concept (No JS)</h1>
    <p>A pure CSS and HTML slideshow concept.</p>
    <p>To add or remove slides:</p>
    <ul>
      <li>Add a new slide template in the HTML</li>
      <li>Update the <code>$slide-count</code> SCSS variable</li>
      <li>Tab colours: Update the <code>$c-slides</code> SCSS variable</li>
      <li>Slide popout images: Update the <code>$b-slides</code> SCSS variable</li>
    </ul>
    <p>Use the tabs below to change slide.</p>
    <p>Et voila.</p>
  </div>  
</div>

<label class="slide-button" for="slideCheckbox2">More</label>
<input type="radio" id="slideCheckbox2" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>More</h1>
    <p>More here</p>
  </div> 
</div>


<label class="slide-button" for="slideCheckbox3">Yet More</label>
<input type="radio" id="slideCheckbox3" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>Yet More</h1>
    <p>Yet more here</p>
  </div>  
</div>

<label class="slide-button" for="slideCheckbox4">Zzz</label>
<input type="radio" id="slideCheckbox4" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>Zzz</h1>
    <p>Yada yada</p>
  </div>   
</div>

<label class="slide-button" for="slideCheckbox5">The End</label>
<input type="radio" id="slideCheckbox5" name="slide"></input>
<div class="slide">
  <div class="slide__content">
    <h1>The end</h1>
    <p>It's over</p>
  </div>  
</div>


</fieldset>


                <ul>
                    <h1 className="title">{this.props.city.charAt(0).toUpperCase() + this.props.city.slice(1)}</h1>
                <article class="widget">
  <div class="weatherIcon"><h1>Today</h1></div>
  <div class="weatherInfo">
    <div class="temperature"><span>{this.state.items["currently"]["summary"]}</span></div>

  </div>

  <div class="date">{day(0)}</div>
</article>
<br/>
<article class="widget">
  <div class="weatherIcon"><h1>Tomorrow</h1></div>
  <div class="weatherInfo">
    <div class="temperature"><span>{this.state.items["daily"]["data"][0]["summary"]}</span></div>

  </div>
  <div class="date">

      <ul>
      {day(1)}
      <br/>
      {"Wind: " + Math.round(this.state.items["daily"]["data"][0]["windSpeed"]) + "kph"}
      </ul>
  </div>
</article>
<br/>
<article class="widget">
  <div class="weatherIcon"><h1>{g()}</h1></div>
  <div class="weatherInfo">
    <div class="temperature"><span>{this.state.items["daily"]["data"][1]["summary"]}</span></div>

  </div>
  <div class="date">
