import React from "react";
import './WeatherInfo.scss'
import {
  useNavigate,
  useLocation
} from "react-router-dom";
import { useEffect, useRef } from "react";
import {ReactScrollableList} from "react-scrollable-list";

const AlwaysScrollToBottom = () => {
    const elementRef = useRef();
    useEffect(() => elementRef.current.scrollIntoView());
    return <div ref={elementRef} />;
  };
function g() {
    var now = new Date();
    var days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    var x = now.getDay();
    if(x+2 > 6) {
        x=x+2-7
    } else {
        x=x+2;
    }
    var day = days[x];
    return day;
}
function day(n) {
    const today = new Date()
    const tomorrow = new Date(today)
    tomorrow.setDate(tomorrow.getDate() + n)
    var days = ['Jan', 'Feb', 'Mar','Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', , 'Oct', 'Nov', 'Dec'];
    return days[tomorrow.getMonth()] + " " + tomorrow.getDate();
}
class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        };
    }
//+json1["latt"]+"," + json1["longt"]
    componentDidMount() {
        fetch(
            "https://geocode.xyz/" + this.props.city.replace(/ /g, '_') + "?json=1")
            .then((res1) => res1.json())
            .then((json1) => {
                fetch(
                    "https://weather-react-api.vercel.app/forecast/coords/40,-71")
                    .then((res) => res.json())
                    .then((json) => {
                        this.setState({
                            items: json,
                            DataisLoaded: true
                        });
                    })
            })
    }
    render() {
        const { DataisLoaded, items } = this.state;
        if (!DataisLoaded) return <div>
            <h1 className="loading"> Loading... </h1> </div> ;
        return (
            <body>

            <h2>Centering</h2>
            <p>In this example, we use positioning and the transform property to vertically and horizontally center the div element:</p>

            <div class="container">
              <div class="center">

                        <h1 className="title">{this.props.city.charAt(0).toUpperCase() + this.props.city.slice(1)}</h1>
                <ul>

                <article class="widget">
  <div class="weatherIcon"><h1>Today</h1></div>
  <div class="weatherInfo">
    <div class="temperature"><span>{this.state.items["currently"]["summary"]}</span></div>

  </div>

  <div class="date">{day(0)}</div>
</article>
<br/>
<article class="widget">
  <div class="weatherIcon"><h1>Tomorrow</h1></div>
  <div class="weatherInfo">
    <div class="temperature"><span>{this.state.items["daily"]["data"][0]["summary"]}</span></div>

  </div>
  <div class="date">

      <ul>
      {day(1)}
      <br/>
      {"Wind: " + Math.round(this.state.items["daily"]["data"][0]["windSpeed"]) + "kph"}
      </ul>
  </div>
</article>
<br/>
<article class="widget">
  <div class="weatherIcon"><h1>{g()}</h1></div>
  <div class="weatherInfo">
    <div class="temperature"><span>{this.state.items["daily"]["data"][1]["summary"]}</span></div>

  </div>
  <div class="date">
      <ul>
      {day(1)}
      <br/>
      {"Wind: " + Math.round(this.state.items["daily"]["data"][1]["windSpeed"]) + "kph"}
      </ul></div>
</article>


            </ul>

              </div>
            </div>

            </body>


    );
}
}
function WeatherInfo() {
        var location = useLocation();

        return (
            <body>
            <App city={location.state.city}/>
            </body>
        );
}

export default WeatherInfo;
