import React from "react";
import './WeatherInfo.scss'
import {
  useNavigate,
  useLocation
} from "react-router-dom";

function g() {
    var now = new Date();
    var days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    var x = now.getDay();
    if(x+2 > 6) {
        x=x+2-7
    } else {
        x=x+2;
    }
    var day = days[x];
    return day;
}
function day(n) {
    const today = new Date()
    const tomorrow = new Date(today)
    tomorrow.setDate(tomorrow.getDate() + n)
    var days = ['Jan', 'Feb', 'Mar','Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', , 'Oct', 'Nov', 'Dec'];
    return days[tomorrow.getMonth()] + " " + tomorrow.getDate();
}
class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        };
    }

    componentDidMount() {
        fetch(
            "https://goweather.herokuapp.com/weather/" + this.props.city)
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    items: json,
                    DataisLoaded: true
                });
            })
    }
    render() {
        const { DataisLoaded, items } = this.state;
        if (!DataisLoaded) return <div>
            <h1> Loading </h1> </div> ;
        if(items["message"]=="NOT_FOUND")
            window.open("/")
        return (
            <div className="ds">
                <ul>
                    <h1 className="title">{this.props.city.charAt(0).toUpperCase() + this.props.city.slice(1)}</h1>
                <article class="widget">
  <div class="weatherIcon"><h1>Today</h1></div>
  <div class="weatherInfo">
    <div class="temperature"><span>{this.state.items["temperature"]}</span></div>
    <div class="description">    
      <div class="weatherCondition">{this.state.items["description"]}</div>    
      <div class="place">{"Wind: " + this.state.items["wind"]}</div>
    </div>
  </div>
  <div class="date">{day(0)}</div>
</article>
<br/>
<article class="widget">
  <div class="weatherIcon"><h1>Tomorrow</h1></div>
  <div class="weatherInfo">
    <div class="temperature"><span>{this.state.items["forecast"][0]["temperature"]}</span></div>
    <div class="description">    
      <div class="weatherCondition">{"Clear"}</div>    
      <div class="place">{"Wind: " + this.state.items["forecast"][0]["wind"]}</div>
    </div>
  </div>
  <div class="date">{day(1)}</div>
</article>
<br/>
<article class="widget">
  <div class="weatherIcon"><h1>{g()}</h1></div>
  <div class="weatherInfo">
    <div class="temperature"><span>{this.state.items["forecast"][1]["temperature"]}</span></div>
    <div class="description">    
      <div class="weatherCondition">{"Clear"}</div>    
      <div class="place">{"Wind: " + this.state.items["forecast"][1]["wind"]}</div>
    </div>
  </div>
  <div class="date">{day(2)}</div>
</article>
            </ul>
            </div>

    );
}
}
function WeatherInfo() {
        var location = useLocation();

        return (
            <body>
            <App city={location.state.city}/>
            </body>
        );
}

export default WeatherInfo;
